const input = $input.first().json.body;
const characterName = input.characterName || '';
const characterClass = input.characterClass || 'Fighter';
const characterRace = input.characterRace || 'Human';
const characterLevel = input.characterLevel || 1;
const spiciness = input.spiciness || 0;
const itemCount = input.itemCount || 5;
const itemTypes = input.itemTypes || [];

let spicyInstruction = '';
if (spiciness < 25) spicyInstruction = 'Keep all items completely family-friendly and suitable for all ages. Standard D&D fantasy items.';
else if (spiciness < 50) spicyInstruction = 'Items can have mildly suggestive or cheeky names and descriptions. Think tavern humor - innuendos and playful double meanings, but nothing explicit.';
else if (spiciness < 75) spicyInstruction = 'Items should have clearly suggestive, flirty, or risqué themes. Adult humor is encouraged.';
else spicyInstruction = 'Items should be boldly adult-themed with explicit humor, provocative descriptions, and unapologetically 18+ content. Be creative and daring with innuendo and adult themes, but maintain D&D fantasy flavor.';

const typeMap = { weapons: 'weapons (swords, axes, bows, daggers, staves, etc.)', armor: 'armor and shields', potions: 'potions and elixirs', scrolls: 'scrolls, spellbooks, and magical tomes', jewelry: 'jewelry (rings, amulets, necklaces)', trinkets: 'trinkets, curiosities, and talismans', clothing: 'clothing, cloaks, boots, and wearable items', tools: 'tools and utility items', consumables: 'consumable items (food, drinks, throwables)' };

let itemTypeInstruction = '';
if (itemTypes.length === 1) itemTypeInstruction = `\nIMPORTANT: ALL ${itemCount} items MUST be ${typeMap[itemTypes[0]] || itemTypes[0]}.`;
else if (itemTypes.length > 1) itemTypeInstruction = `\nIMPORTANT: Items MUST be from these categories ONLY: ${itemTypes.map(t => typeMap[t] || t).join(', ')}.`;

const nameLine = characterName ? `- Character Name: ${characterName}\n` : '';
const userPrompt = `Generate exactly ${itemCount} unique starting items for a D&D 5e (2024 edition) character:\n\n${nameLine}- Class: ${characterClass}\n- Race: ${characterRace}\n- Level: ${characterLevel}\n${itemTypeInstruction}\n\nSpiciness Level: ${spiciness}/100\n${spicyInstruction}\n\nFor each item provide: "name", "type", "rarity" (common/uncommon/rare/very-rare/legendary), "description" (1-3 sentences), "properties" (array of 1-3 effects).\n\nRules: Mix item types for the class. Level 1 = mostly common/uncommon. Include at least one trinket with backstory flavor. Use Forgotten Realms lore.\n\nRespond with a JSON array. No markdown, no code fences.`;

return [{ json: { userPrompt, characterName, characterClass, spiciness } }];

const aiResponse = $input.first().json.output;
if (!aiResponse) throw new Error('No response from AI Agent');
try {
  let cleaned = aiResponse.trim();
  if (cleaned.startsWith('```json')) cleaned = cleaned.slice(7);
  if (cleaned.startsWith('```')) cleaned = cleaned.slice(3);
  if (cleaned.endsWith('```')) cleaned = cleaned.slice(0, -3);
  cleaned = cleaned.trim();
  const items = JSON.parse(cleaned);
  return [{ json: { items: Array.isArray(items) ? items : [items] } }];
} catch (error) {
  const match = aiResponse.match(/\[\s*\{[\s\S]*\}\s*\]/);
  if (match) { try { return [{ json: { items: JSON.parse(match[0]) } }]; } catch(e) {} }
  return [{ json: { items: [{ name: 'Mysterious Error Scroll', type: 'scroll', rarity: 'common', description: 'Something went wrong in the magical forge.', properties: ['Try generating again'] }] } }];
}

