// Extract input from the webhook body
const input = $input.first().json.body;

const characterName = input.characterName || 'Unnamed Hero';
const characterClass = input.characterClass || 'Fighter';
const characterRace = input.characterRace || 'Human';
const characterLevel = input.characterLevel || 1;
const spiciness = input.spiciness || 0;
const itemCount = input.itemCount || 5;

// Determine spiciness instruction
let spicyInstruction = '';
if (spiciness < 25) {
  spicyInstruction = 'Keep all items completely family-friendly and suitable for all ages. Standard D&D fantasy items.';
} else if (spiciness < 50) {
  spicyInstruction = 'Items can have mildly suggestive or cheeky names and descriptions. Think tavern humor - innuendos and playful double meanings, but nothing explicit.';
} else if (spiciness < 75) {
  spicyInstruction = 'Items should have clearly suggestive, flirty, or risquÃ© themes. Adult humor is encouraged - think burlesque D&D. Names and descriptions can be provocative but not vulgar.';
} else {
  spicyInstruction = 'Items should be boldly adult-themed with explicit humor, provocative descriptions, and unapologetically 18+ content. Be creative and daring with innuendo and adult themes, but maintain a sense of humor and D&D fantasy flavor.';
}

const userPrompt = `Generate exactly ${itemCount} unique starting items for a new character:

- Character Name: ${characterName}
- Class: ${characterClass}
- Race: ${characterRace}
- Level: ${characterLevel}

Spiciness Level: ${spiciness}/100
${spicyInstruction}

For each item provide these JSON fields:
- "name": A creative item name
- "type": Item category (weapon, armor, potion, trinket, tool, clothing, misc, scroll, ring, amulet, etc.)
- "rarity": One of: common, uncommon, rare, very-rare, legendary
- "description": A flavorful 1-3 sentence description
- "properties": Array of 1-3 mechanical properties or fun effects

Rules:
- Mix item types appropriate for the class
- For level 1 characters, most items should be common/uncommon
- At least one trinket or personal item with backstory flavor
- Items should feel thematic to the class and race

Respond with a JSON array of ${itemCount} item objects.`;

return [{
  json: {
    userPrompt,
    characterName,
    characterClass,
    spiciness
  }
}];

// Extract the AI Agent response
const aiResponse = $input.first().json.output;

if (!aiResponse) {
  throw new Error('No response from AI Agent');
}

try {
  // Clean up potential markdown code fences
  let cleaned = aiResponse.trim();
  if (cleaned.startsWith('```json')) cleaned = cleaned.slice(7);
  if (cleaned.startsWith('```')) cleaned = cleaned.slice(3);
  if (cleaned.endsWith('```')) cleaned = cleaned.slice(0, -3);
  cleaned = cleaned.trim();

  const items = JSON.parse(cleaned);

  if (!Array.isArray(items)) {
    return [{ json: { items: [items] } }];
  }

  return [{ json: { items } }];

} catch (error) {
  // Fallback: try to extract JSON array from the response
  const match = aiResponse.match(/\[\s*\{[\s\S]*\}\s*\]/);
  if (match) {
    try {
      const items = JSON.parse(match[0]);
      return [{ json: { items } }];
    } catch (e) {
      // Cannot parse
    }
  }

  // Return a fallback error item
  return [{ json: { items: [{
    name: 'Mysterious Error Scroll',
    type: 'scroll',
    rarity: 'common',
    description: 'Something went wrong in the magical forge. The AI returned an unexpected response.',
    properties: ['Try generating again']
  }] } }];
}
